import React, { Component } from 'react';
import { useHistory , Link } from 'react-router-dom';
import validator from 'validator';
import axios from 'axios';

class Blog extends Component {
		
    constructor(props) {
		super(props);
		this.state = {
			fields : {
				title : '',
				content : '',
				url : '' ,
				meta : '' ,
				created : ''
			},
			errors : {}
		}
	}
		
	handleValidation(callback) {
        let { fields } = this.state;
        let errors = {};
        let formIsValid = true;
		
		
		

    }
    handleChange(event) {
        let fields = this.state.fields;
        let target = event.target;
        fields[target.name] = target.value;
        this.setState({fields});
    }
	
	 handleRequest() {
		
		
		
        const { title , content , url , meta , created } = this.state.fields;

        let data = new FormData();
        data.append('title' , title);
        data.append('content' , content);
		data.append('url' , url);
		data.append('meta' , meta);
		data.append('created' , created);

        axios.post('http://farmaniehdrycleaning.com/api_rest/rest/product/blog.php' , data)
		
            .then(response => {
                
				
					
				 
				//console.log(response.data.api_token);
            })
            .catch(error => {
                console.log(error)
            })
    }
	
	  render() {	
        return (
            <div>
                <h2>Blog Form</h2>
                <form onSubmit={this.handleSubmit.bind(this)} className="col-lg-5" style={{ marginTop : 20}}>
					<div className="form-group">
                        <label>Title : </label>
                        <input
                            type="text"
                            className={["form-control" , errors["title"] ? 'is-invalid' : ''].join(' ')}
                            name="title"
                            value={title}
                            onChange={this.handleChange.bind(this)}
                            placeholder="Please enter your title"/>
                        <span className="invalid-feedback rtl" style={{ display : errors["title"] ? 'block' : 'none'}}>{errors["title"]}</span>
                    </div>
					
					<div className="form-group">
                        <label>Content : </label>
                        <input
                            type="text"
                            className={["form-control" , errors["content"] ? 'is-invalid' : ''].join(' ')}
                            name="content"
                            value={content}
                            onChange={this.handleChange.bind(this)}
                            placeholder="Please enter your content"/>
                        <span className="invalid-feedback rtl" style={{ display : errors["content"] ? 'block' : 'none'}}>{errors["content"]}</span>
                    </div>
					
					
					<div className="form-group">
                        <label>URL Slug : </label>
                        <input
                            type="text"
                            className={["form-control" , errors["url"] ? 'is-invalid' : ''].join(' ')}
                            name="url"
                            value={url}
                            onChange={this.handleChange.bind(this)}
                            placeholder="Please enter your url"/>
                        <span className="invalid-feedback rtl" style={{ display : errors["url"] ? 'block' : 'none'}}>{errors["url"]}</span>
                    </div>
					
					
					<div className="form-group">
                        <label>Meta Description : </label>
                        <input
                            type="text"
                            className={["form-control" , errors["meta"] ? 'is-invalid' : ''].join(' ')}
                            name="meta"
                            value={meta}
                            onChange={this.handleChange.bind(this)}
                            placeholder="Please enter your meta"/>
                        <span className="invalid-feedback rtl" style={{ display : errors["meta"] ? 'block' : 'none'}}>{errors["meta"]}</span>
                    </div>
					
					
					<div className="form-group">
                        <label>Created : </label>
                        <input
                            type="text"
                            className={["form-control" , errors["created"] ? 'is-invalid' : ''].join(' ')}
                            name="created"
                            value={created}
                            onChange={this.handleChange.bind(this)}
                            placeholder="Please enter your created"/>
                        <span className="invalid-feedback rtl" style={{ display : errors["created"] ? 'block' : 'none'}}>{errors["created"]}</span>
                    </div>
					
                    <div className="form-group">
                        <button className="btn btn-danger" type="submit">Submit</button>
                    </div>
                </form>
            </div>
        );
	  }
}

export default Blog;
